import React, { useState } from 'react'
import { Link } from "react-scroll"
import './Header.css'

const Header = () => {
    const [menuOpened,setMenuOpened] =useState(false)
  return (
    <div className='header-container'>
        {menuOpened === false ? (
            <div onClick={()=> setMenuOpened(true)}></div>
        ):(
        <ul className='header-menu'>
            <li><Link onClick={()=> setMenuOpened(true)} to='home' span={true} smooth={true}>Home</Link></li>
            <li><Link to='home' span={true} smooth={true}>About Me</Link></li>
            <li><Link to='home' span={true} smooth={true}>Contact Me</Link></li>
        </ul>
        )}
    </div>
  )
}

export default Header
